mapper.insert:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.saveApplypayment */
SELECT CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'), '-', LPAD(IFNULL(COUNT(SETTLEMENT_SEQ), 0) + 1, '4', '0')) AS SETTLEMENT_SEQ FROM SETTLEMENT WHERE REQ_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d')
INSERT INTO SETTLEMENT ( SETTLEMENT_SEQ, PROD_ID, USER_ID, CUST_ID, REQ_DATE, PROD_AMT, SALE_AMT, INFLOW_CNT, SALE_TOTAL_AMT, REQ_GUBUN, DEPOSIT_AMT, DEPOSIT_REAL_AMT, DATE_WORK_FROM, DATE_WORK_TO, WORK_DAY, INCENTIVE_RATE, EMP_PAYMENT, REFUND_IND, ATCH_ID, STATUS, TAX_IND, PAY_YM ) VALUES ( #{settlementSeq}, #{prodId}, #{loginUserId}, #{custId}, DATE_FORMAT(NOW(), '%Y-%m-%d'), #{prodAmt}, #{saleAmt}, #{inflowCnt}, #{saleTotalAmt}, #{reqGubun}, #{depositAmt}, #{depositRealAmt}, #{dateWorkFrom}, #{dateWorkTo}, #{workDay}, #{incentiveRate}, #{empPayment}, #{refundInd}, #{atchId}, #{status}, #{taxInd}, #{payYm} )

mapper.delete:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.deleteCustomer */
DELETE FROM TB_CUSTOMER
WHERE BIZ_NO    = #{bizNo}
  AND EMP_ID   = #{empId}
