mapper.insert:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.saveApplypayment */
SELECT CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'), '-', LPAD(IFNULL(COUNT(SETTLEMENT_SEQ), 0) + 1, '4', '0')) AS SETTLEMENT_SEQ FROM SETTLEMENT WHERE REQ_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d')
INSERT INTO SETTLEMENT ( SETTLEMENT_SEQ, PROD_ID, USER_ID, CUST_ID, REQ_DATE, PROD_AMT, SALE_AMT, INFLOW_CNT, SALE_TOTAL_AMT, REQ_GUBUN, EXPECT_AMT, EXPECT_RATE_AMT, DATE_WORK_FROM, DATE_WORK_TO, WORK_DAY, INCENTIVE_RATE, ACCT_AMT, REFUND_IND, ATCH_ID, STATUS, TAX_IND, PAY_YM ) VALUES ( #{settlementSeq}, #{prodId}, #{loginUserId}, #{custId}, DATE_FORMAT(NOW(), '%Y-%m-%d'), #{prodAmt}, #{saleAmt}, #{inflowCnt}, #{saleTotalAmt}, #{reqGubun}, #{expectAmt}, #{expectRateAmt}, #{dateWorkFrom}, #{dateWorkTo}, #{workDay}, #{incentiveRate}, #{acctAmt}, #{refundInd}, #{atchId}, #{status}, #{taxInd}, #{payYm} )

mapper.delete:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.deleteCustomer */
DELETE FROM TB_CUSTOMER
WHERE BIZ_NO    = #{bizNo}
  AND EMP_ID   = #{empId}

mapper.select:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.findSettlement */ SELECT ST.SETTLEMENT_SEQ , ST.PROD_ID , PR.PROD_NAME , ST.USER_ID , EP.EMP_NAME , ST.CUST_ID , CU.CUST_NAME , ST.REQ_DATE , ST.PROD_AMT , ST.SALE_AMT , ST.INFLOW_CNT , ST.SALE_TOTAL_AMT , ST.REQ_GUBUN , ST.EXPECT_AMT , ST.EXPECT_RATE_AMT , ST.CONFIRM_AMT , ST.CONFIRM_RATE_AMT , ST.USE_MILEAGE , ST.SAVE_MILEAGE , DATE_FORMAT(ST.DATE_WORK_FROM, '%Y-%m-%d') AS DATE_WORK_FROM , DATE_FORMAT(ST.DATE_WORK_TO, '%Y-%m-%d') AS DATE_WORK_TO , ST.WORK_DAY , IFNULL(ST.INCENTIVE_RATE, 0) AS INCENTIVE_RATE , ST.REFUND_IND , ST.ATCH_ID , ST.STATUS , CD.CODE_NAME AS STATUS_NAME , ST.TAX_IND , ST.PAY_YM FROM SETTLEMENT ST LEFT JOIN TB_CUSTOMER CU ON ST.CUST_ID = CU.BIZ_NO LEFT JOIN TB_PRODUCTS PR ON ST.PROD_ID = PR.PROD_ID AND DATE_FORMAT(ST.REQ_DATE, '%Y%m%d') BETWEEN PR.EFF_DATE_FROM AND PR.EFF_DATE_TO LEFT JOIN TB_EMP EP ON ST.USER_ID = EP.EMP_ID LEFT JOIN CDBASE CD ON CD.PATTERN_CODE = 'SE02' AND CD.BASE_CODE = ST.STATUS WHERE 1=1
AND ST.SETTLEMENT_SEQ LIKE CONCAT('%', #{settlementSeq}, '%')
AND #{searchStartDate} >= ST.WORK_DATE_FROM
AND #{searchEndDate} <= ST.WORK_DATE_TO
AND ST.STATUS = #{status}
