mapper.select:
/* com.biz.framework.mapper.pages.ApplypaymentapprmngMapper.findApplypaymentmngList */
SELECT ST.SETTLEMENT_SEQ, ST.PROD_ID, PR.PROD_NAME, ST.USER_ID, EP.EMP_NAME, ST.CUST_ID, CU.CUST_NAME, ST.REQ_DATE, ST.PROD_AMT, ST.SALE_AMT, ST.INFLOW_CNT, ST.PROD_TOTAL_AMT, ST.SALE_TOTAL_AMT, ST.EXPECT_AMT, ST.EXPECT_RATE_AMT, DATE_FORMAT(ST.DATE_WORK_FROM, '%Y-%m-%d') AS DATE_WORK_FROM, DATE_FORMAT(ST.DATE_WORK_TO, '%Y-%m-%d') AS DATE_WORK_TO, ST.WORK_DAY, IFNULL(ST.INCENTIVE_RATE, 0) AS INCENTIVE_RATE, ST.USE_MILEAGE, DATE_FORMAT(ST.REFUND_DATE, '%Y-%m-%d') AS REFUND_DATE, ST.REQ_GUBUN, CD1.CODE_NAME AS REQ_GUBUN_NAME, ST.APPLY_STATUS, CD2.CODE_NAME AS STATUS_NAME, ST.REFUND_IND, IFNULL(SM.CONFIRM_AMT, 0) AS CONFIRM_AMT, SM.CONFIRM_MILEAGE, SM.CONFIRM_RATE_AMT, SM.CONFIRM_DATE, SM.CONFIRM_IND, SM.TAX_IND, SM.PAY_YM, SM.ATCH_ID, SM.REMARK FROM SETTLEMENT ST LEFT JOIN SETTLEMENTMST SM ON ST.SETTLEMENT_SEQ = SM.SETTLEMENT_SEQ LEFT JOIN TB_CUSTOMER CU ON ST.CUST_ID = CU.BIZ_NO LEFT JOIN TB_PRODUCTS PR ON ST.PROD_ID = PR.PROD_ID AND DATE_FORMAT(ST.REQ_DATE, '%Y%m%d') BETWEEN PR.EFF_DATE_FROM AND PR.EFF_DATE_TO LEFT JOIN TB_EMP EP ON ST.USER_ID = EP.EMP_ID LEFT JOIN CDBASE CD1 ON CD1.PATTERN_CODE = 'SE01' AND ST.REQ_GUBUN = CD1.BASE_CODE LEFT JOIN CDBASE CD2 ON CD2.PATTERN_CODE = 'SE04' AND ST.APPLY_STATUS = CD2.BASE_CODE WHERE 1=1 AND (DATE_FORMAT(#{searchStartDate} , '%Y%m%d') <= ST.DATE_WORK_FROM) AND (DATE_FORMAT(#{searchEndDate} , '%Y%m%d') >= ST.DATE_WORK_TO)
AND PR.PROD_NAME LIKE CONCAT('%',#{prodName} ,'%')
AND EP.EMP_NAME LIKE CONCAT('%',#{empName} ,'%')
AND CU.CUST_NAME LIKE CONCAT('%',#{custName} ,'%')
AND ST.APPLY_STATUS = #{applyStatus}
AND ST.REQ_GUBUN = #{reqGubun}

/* com.biz.framework.mapper.pages.ApplypaymentMapper.checkPayrollInd */
SELECT COUNT(*) FROM SETTLEMENTMST
WHERE SETTLEMENT_SEQ = #{settlementSeq}
AND PAY_YM IS NOT NULL
AND PAY_YM != ''

/* com.biz.framework.mapper.pages.ApplypaymentMapper.checkPayrollInd */
SELECT COUNT(*) FROM SETTLEMENTMST
WHERE SETTLEMENT_SEQ = #{settlementSeq}

mapper.update:
UPDATE SETTLEMENT
SET APPLY_STATUS = #{applyStatus}
WHERE SETTLEMENT_SEQ = #{settlementSeq}
