mapper.select:
/* com.biz.framework.mapper.pages.EmpCustomerMapper.findEmplList */ SELECT T.* FROM TB_EMP T WHERE 1=1 AND T.EMP_ID != 'admin'
AND T.EMP_ID LIKE CONCAT('%', #{searchEmpId}, '%')
AND T.EMP_NAME LIKE CONCAT('%', #{searchEmpNm}, '%')
AND T.EMP_ID = #{searchEmpId}

mapper.insert:
/* com.biz.framework.mapper.pages.EmpCustomerMapper.saveEmpCustomerList */
INSERT INTO EMP_CUSTOMER(
    EMP_ID
    , BIZ_NO
) VALUES (
    #{empId}
    , #{bizNo}
)

mapper.select:
/* com.biz.framework.mapper.pages.EmpCustomerMapper.saveEmpCustomerList */
SELECT COUNT(1)
  FROM EMP_CUSTOMER
 WHERE EMP_ID   = #{empId}
   AND BIZ_NO   = #{bizNo}

mapper.delete:
/* com.biz.framework.mapper.pages.EmpCustomerMapper.saveEmpCustomerList */
DELETE FROM EMP_CUSTOMER
WHERE EMP_ID    = #{empId}
  AND BIZ_NO    = #{bizNo}
