mapper.select:
/* com.biz.framework.mapper.pages.EmpMapper.findEmps */ SELECT T.* , C.CODE_NAME AS GRADE_NAME FROM TB_EMP T LEFT JOIN CDBASE C ON C.PATTERN_CODE = 'CD01' AND C.BASE_CODE = T.GRADE_CODE WHERE 1=1 AND EMP_ID != 'admin'
AND T.EMP_ID LIKE CONCAT('%',#{empId} ,'%')
AND T.EMP_NAME LIKE CONCAT('%',#{empName} ,'%')

mapper.insert:
/* com.biz.framework.mapper.pages.EmpMapper.saveEmp */
INSERT INTO TB_EMP
(
    EMP_ID
    , EMP_NAME
    , ENTRANCE_DATE
    , RETIRE_DATE
    , GRADE_CODE
    , EMP_TEL
    , EMP_MAIL
    , INCENTIVE_RATE
) VALUES
(
    #{empId}
    , #{empName}
    , STR_TO_DATE(#{entranceDate}, '%Y-%m-%d')
    , IFNULL(STR_TO_DATE(#{retireDate}, '%Y-%m-%d'), '2999-12-31')
    , #{gradeCode}
    , #{empTel}
    , #{empMail}
    , #{incentiveRate}
) ON DUPLICATE KEY UPDATE
    EMP_NAME            = #{empName}
    , ENTRANCE_DATE     = STR_TO_DATE(#{entranceDate}, '%Y-%m-%d')
    , RETIRE_DATE       = STR_TO_DATE(#{retireDate}, '%Y-%m-%d')
    , GRADE_CODE        = #{gradeCode}
    , EMP_TEL           = #{empTel}
    , EMP_MAIL          = #{empMail}
    , INCENTIVE_RATE    = #{incentiveRate}

mapper.select:
/* com.biz.framework.mapper.pages.EmpMapper.checkDuplEmpId */
SELECT COUNT(1)
 FROM TB_EMP
WHERE EMP_ID = #{empId}
