mapper.select:
/* com.biz.framework.mapper.pages.EmpMapper.findAllEmps */
SELECT EMP_ID
     , EMP_NAME
  FROM TB_EMP
 WHERE CO_CODE       = #{loginCoId}
   AND EMP_ID       != 'admin'

/* com.biz.framework.mapper.pages.EmpMapper.findEmps */ SELECT T.* , U.USER_PW , C.CODE_NAME AS GRADE_NAME FROM TB_EMP T JOIN USER U ON U.CO_CODE = T.CO_CODE AND T.EMP_ID = U.USER_ID LEFT JOIN CDBASE C ON C.CO_CODE = T.CO_CODE AND C.PATTERN_CODE = 'CD01' AND C.BASE_CODE = T.GRADE_CODE WHERE 1=1 AND T.CO_CODE = #{loginCoId} AND EMP_ID != 'admin'
AND T.EMP_ID LIKE CONCAT('%',#{empId} ,'%')
AND T.EMP_NAME LIKE CONCAT('%',#{empName} ,'%')

mapper.insert:
/* com.biz.framework.mapper.pages.EmpMapper.saveEmp */
INSERT INTO TB_EMP
(
    CO_CODE
    , EMP_ID
    , EMP_NAME
    , ENTRANCE_DATE
    , RETIRE_DATE
    , GRADE_CODE
    , EMP_TEL
    , EMP_MAIL
    , INCENTIVE_RATE
) VALUES
(
    #{loginCoId}
    , #{empId}
    , #{empName}
    , STR_TO_DATE(#{entranceDate}, '%Y-%m-%d')
    , IFNULL(STR_TO_DATE(#{retireDate}, '%Y-%m-%d'), '2999-12-31')
    , #{gradeCode}
    , #{empTel}
    , #{empMail}
    , #{incentiveRate}
) ON DUPLICATE KEY UPDATE
    EMP_NAME            = #{empName}
    , ENTRANCE_DATE     = STR_TO_DATE(#{entranceDate}, '%Y-%m-%d')
    , RETIRE_DATE       = STR_TO_DATE(#{retireDate}, '%Y-%m-%d')
    , GRADE_CODE        = #{gradeCode}
    , EMP_TEL           = #{empTel}
    , EMP_MAIL          = #{empMail}
    , INCENTIVE_RATE    = #{incentiveRate}

mapper.delete:
/* com.biz.framework.mapper.pages.EmpMapper.deleteEmp */
DELETE FROM TB_EMP
WHERE CO_CODE   = #{loginCoId}
  AND EMP_ID    = #{empId}

mapper.select:
/* com.biz.framework.mapper.pages.EmpMapper.checkDuplEmpId */
SELECT COUNT(1)
 FROM TB_EMP
WHERE CO_CODE   = #{loginCoId}
  AND EMP_ID    = #{empId}
