mapper.select:
/* com.biz.framework.mapper.pages.EmpMapper.findEmps */ SELECT T.* FROM TB_EMP T WHERE 1=1
AND T.EMP_ID LIKE CONCAT('%',#{empId} ,'%')
AND T.EMP_NAME LIKE CONCAT('%',#{empName} ,'%')

mapper.insert:
/* com.biz.framework.mapper.pages.EmpMapper.saveCustomer */
INSERT INTO TB_EMP
(
    EMP_ID
    , EMP_NAME
    , ENTRANCE_DATE
    , RETIRE_DATE
    , GRADE_CODE
    , EMP_TEL
    , EMP_MAIL
    , INCENTIVE_RATE
) VALUES
(
    #{empId}
    , #{empName}
    , STR_TO_DATE(#{entranceDate}, '%Y-%m-%d')
    , STR_TO_DATE(#{retireDate}, '%Y-%m-%d')
    , #{gradeCode}
    , #{empTel}
    , #{empMail}
    , #{incentiveRate}
) ON DUPLICATE KEY UPDATE
    EMP_NAME            = #{empName}
    , ENTRANCE_DATE     = STR_TO_DATE(#{entranceDate}, '%Y-%m-%d')
    , RETIRE_DATE       = STR_TO_DATE(#{retireDate}, '%Y-%m-%d')
    , GRADE_CODE        = #{gradeCode}
    , EMP_TEL           = #{empTel}
    , EMP_MAIL          = #{empMail}
    , INCENTIVE_RATE    = #{incentiveRate}

mapper.select:
/* com.biz.framework.mapper.pages.EmpMapper.checkDuplEmpId */
SELECT COUNT(1)
 FROM TB_EMP
WHERE EMP_ID = #{empId}
