mapper.insert:
/* com.biz.framework.mapper.pages.MileageHisMapper.saveMileageHis */
INSERT INTO TB_MILEAGE_HIS
(
    BIZ_NO
    , EMP_ID
    , MILEAGE_PREV
    , MILEAGE_AFT
    , CREATED_DATE
    , CREATED_PAGE
    , CREATED_ID
    , SETTLEMENT_SEQ
) VALUES
(
    #{bizNo}
    , #{empId}
    , #{mileagePrev}
    , #{mileageAft}
    , NOW()
    , #{createdPage}
    , #{createdId}
    , #{settlementSeq}
)

mapper.delete:
/* com.biz.framework.mapper.pages.MileageHisMapper.deleteMileageHisByBizNoEmpId */
DELETE FROM TB_MILEAGE_HIS
WHERE BIZ_NO     = #{bizNo}
  AND EMP_ID     = #{empId}
