mapper.select:
/* com.biz.framework.mapper.pages.ProductMapper.findProducts */ SELECT PROD_ID , PROD_NAME , PROD_AMT , USE_IND , SE06.CODE_NAME AS USE_IND_NAME FROM TB_PRODUCTS P LEFT JOIN CDBASE SE06 ON SE06.CO_CODE = P.CO_CODE AND SE06.PATTERN_CODE = 'SE06' AND SE06.BASE_CODE = P.USE_IND WHERE 1=1 AND P.CO_CODE = #{loginCoId}
AND PROD_ID LIKE CONCAT('%', #{prodId}, '%')
AND PROD_NAME LIKE CONCAT('%', #{prodName}, '%')
AND USE_IND = #{useInd}

/* com.biz.framework.mapper.pages.ProductMapper.findProductsInfo */
SELECT
PROD_ID
, PROD_NAME
, PROD_AMT
, USE_IND
FROM TB_PRODUCTS    P
WHERE 1=1
AND P.CO_CODE       = #{loginCoId}
AND PROD_ID         = #{prodId}

mapper.insert:
/* com.biz.framework.mapper.pages.ProductMapper.saveProducts */
INSERT INTO TB_PRODUCTS
(
    CO_CODE
    , PROD_ID
    , PROD_NAME
    , PROD_AMT
    , USE_IND
) VALUES
(
    #{loginCoId}
    , #{prodId}
    , #{prodName}
    , #{prodAmt}
    , #{useInd}
) ON DUPLICATE KEY UPDATE
 PROD_NAME         = #{prodName}
, PROD_AMT          = #{prodAmt}
, USE_IND           = #{useInd}

mapper.delete:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.deleteProducts */
DELETE FROM TB_PRODUCTS
 WHERE CO_CODE          = #{loginCoId}
   AND PROD_ID          = #{prodId}

mapper.select:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.checkDuplProduct */
SELECT COUNT(1)
  FROM TB_PRODUCTS
 WHERE CO_CODE      = #{loginCoId}
   AND PROD_ID      = #{prodId}

mapper.update:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.updateProductEffDate */
UPDATE TB_PRODUCTS
   SET EFF_DATE_TO  = DATE_ADD(STR_TO_DATE(#{effDateFrom}, '%Y-%m-%d'), INTERVAL -1 DAY)
 WHERE CO_CODE      = #{loginCoId}
   AND PROD_ID      = #{prodId}
   AND #{effDateFrom} <= EFF_DATE_TO
   AND #{effDateTo} >= EFF_DATE_FROM

mapper.select:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.createProdId */
SELECT CONCAT('PD-', LPAD(IFNULL(MAX(cast(SUBSTRING_INDEX(PROD_ID, '-', -1) AS UNSIGNED INTEGER)), 0) + 1, 4, '0'))
  FROM TB_PRODUCTS	TP
 WHERE TP.CO_CODE 	= #{loginCoId}
