mapper.select:
/* com.biz.framework.mapper.pages.ProductMapper.findProducts */ SELECT PROD_ID , PROD_NAME , PROD_AMT , DATE_FORMAT(EFF_DATE_FROM, '%Y-%m-%d') AS EFF_DATE_FROM , DATE_FORMAT(EFF_DATE_TO, '%Y-%m-%d') AS EFF_DATE_TO , USE_IND FROM TB_PRODUCTS P WHERE 1=1
AND PROD_ID LIKE CONCAT('%', #{prodId}, '%')
AND PROD_NAME LIKE CONCAT('%', #{prodName}, '%')

mapper.insert:
/* com.biz.framework.mapper.pages.ProductMapper.saveProducts */
INSERT INTO TB_PRODUCTS
(
    PROD_ID
    , PROD_NAME
    , PROD_AMT
    , EFF_DATE_FROM
    , EFF_DATE_TO
    , USE_IND
) VALUES
(
    #{prodId}
    , #{prodName}
    , #{prodAmt}
    , STR_TO_DATE(#{effDateFrom}, '%Y-%m-%d')
    , STR_TO_DATE(#{effDateTo}, '%Y-%m-%d')
    , #{useInd}
) ON DUPLICATE KEY UPDATE
  PROD_NAME         = #{prodName}
, PROD_AMT          = #{prodAmt}
, EFF_DATE_FROM     = STR_TO_DATE(#{effDateFrom}, '%Y-%m-%d')
, EFF_DATE_TO       = STR_TO_DATE(#{effDateTo}, '%Y-%m-%d')
, USE_IND           = #{useInd}
