mapper.select:
/* com.biz.framework.mapper.pages.ProductMapper.findProducts */ SELECT PROD_ID , PROD_NAME , PROD_AMT , DATE_FORMAT(EFF_DATE_FROM, '%Y-%m-%d') AS EFF_DATE_FROM , DATE_FORMAT(EFF_DATE_TO, '%Y-%m-%d') AS EFF_DATE_TO , USE_IND FROM TB_PRODUCTS P WHERE 1=1 AND STR_TO_DATE(#{baseDate}, '%Y-%m-%d') BETWEEN P.EFF_DATE_FROM AND P.EFF_DATE_TO
AND PROD_ID LIKE CONCAT('%', #{prodId}, '%')
AND PROD_NAME LIKE CONCAT('%', #{prodName}, '%')

mapper.insert:
/* com.biz.framework.mapper.pages.ProductMapper.saveProducts */
INSERT INTO TB_PRODUCTS
(
    PROD_ID
    , EFF_DATE_FROM
    , EFF_DATE_TO
    , PROD_NAME
    , PROD_AMT
    , USE_IND
) VALUES
(
    #{prodId}
    , STR_TO_DATE(#{effDateFrom}, '%Y-%m-%d')
    , STR_TO_DATE(#{effDateTo}, '%Y-%m-%d')
    , #{prodName}
    , #{prodAmt}
    , #{useInd}
) ON DUPLICATE KEY UPDATE
  EFF_DATE_TO       = STR_TO_DATE(#{effDateTo}, '%Y-%m-%d')
, PROD_NAME         = #{prodName}
, PROD_AMT          = #{prodAmt}
, USE_IND           = #{useInd}

mapper.delete:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.deleteProducts */
DELETE FROM TB_PRODUCTS
WHERE PROD_ID       = #{prodId}
AND EFF_DATE_FROM   = #{effDateFrom}

mapper.select:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.checkDuplProduct */
SELECT COUNT(1)
  FROM TB_PRODUCTS
 WHERE PROD_ID = #{prodId}
   AND #{effDateFrom} <= EFF_DATE_TO
   AND #{effDateTo} >= EFF_DATE_FROM

mapper.update:
/* com.biz.framework.mapper.pages.ApplypaymentMapper.updateProductEffDate */
UPDATE TB_PRODUCTS
   SET EFF_DATE_TO  = DATE_ADD(STR_TO_DATE(#{effDateFrom}, '%Y-%m-%d'), INTERVAL -1 DAY)
 WHERE PROD_ID  = #{prodId}
   AND #{effDateFrom} <= EFF_DATE_TO
   AND #{effDateTo} >= EFF_DATE_FROM
