mapper.select:
/* com.biz.framework.mapper.system.CodeMapper.checkCdpatt */
SELECT COUNT(1)
FROM CDPATT
WHERE PATTERN_CODE      = #{patternCode}

mapper.insert:
/* com.biz.framework.mapper.system.CodeMapper.saveCdpatt */
INSERT INTO CDPATT (
    PATTERN_CODE
    , PATTERN_NAME
) VALUES (
    #{patternCode}
    , #{patternName}
)

mapper.update:
/* com.biz.framework.mapper.system.CodeMapper.updateCdpatt */
UPDATE CDPATT
   SET PATTERN_NAME     = #{patternName}
 WHERE PATTERN_CODE     = #{patternCode}

mapper.select:
/* com.biz.framework.mapper.system.CodeMapper.checkCdbase */
SELECT COUNT(1)
FROM CDBASE
WHERE PATTERN_CODE  = #{patternCode}
  AND BASE_CODE     = #{baseCode}

mapper.insert:
/* com.biz.framework.mapper.system.CodeMapper.saveCdbase */
INSERT INTO CDBASE (
    PATTERN_CODE
    , BASE_CODE
    , CODE_NAME
    , DISPLAY_ORDER
) VALUES (
    #{patternCode}
    , #{baseCode}
    , #{codeName}
    , #{displayOrder}
)

mapper.delete:
/* com.biz.framework.mapper.system.CodeMapper.saveCdbase */
DELETE FROM CDBASE
 WHERE PATTERN_CODE = #{patternCode}
   AND BASE_CODE    = #{baseCode}
