mapper.select:
/* com.biz.framework.mapper.ProgramMapper.findProgramList */ SELECT PROGRAM_ID , PROGRAM_NM , PROGRAM_URL , USE_YN , PROGRAM_SRT_ORD FROM PROGRAM WHERE 1=1
AND PROGRAM_NM LIKE CONCAT('%', #{programNm}, '%') OR PROGRAM_URL LIKE CONCAT('%', #{programNm}, '%')
ORDER BY PROGRAM_SRT_ORD

mapper.insert:
/* com.biz.framework.mapper.ProgramMapper.saveProgramList */
INSERT INTO PROGRAM (
    PROGRAM_NM
    , PROGRAM_URL
    , USE_YN
    , PROGRAM_SRT_ORD
) VALUES (
    #{programNm}
    , #{programUrl}
    , #{useYn}
    , #{programSrtOrd}
)

mapper.update:
/* com.biz.framework.mapper.ProgramMapper.updateProgramList */
UPDATE PROGRAM
   SET PROGRAM_NM       = #{programNm}
     , PROGRAM_URL      = #{programUrl}
     , USE_YN           = #{useYn}
     , PROGRAM_SRT_ORD  = #{programSrtOrd}
 WHERE PROGRAM_ID       = #{programId}

mapper.select:
/* com.biz.framework.mapper.ProgramMapper.checkMenuPgmDupl */
SELECT COUNT(1)
FROM MENU_PROGRAM
WHERE MENU_ID      = #{menuId}
AND PROGRAM_ID   = #{programId}

mapper.insert:
/* com.biz.framework.mapper.ProgramMapper.saveMenuProgramList */
INSERT INTO MENU_PROGRAM (
    MENU_ID
    , PROGRAM_ID
) VALUES (
    #{menuId}
    , #{programId}
)

mapper.delete:
/* com.biz.framework.mapper.ProgramMapper.deleteMenuProgramList */
DELETE FROM MENU_PROGRAM
 WHERE PROGRAM_ID   = #{programId}
   AND MENU_ID      = #{menuId}
