mapper.select:
/* com.biz.framework.mapper.system.UserMapper.findAllUsers */
SELECT USER_ID
     , USER_NM
  FROM USER
 WHERE CO_CODE  = #{loginCoId}

/* com.biz.framework.mapper.system.UserMapper.findByUserId */
SELECT CO_CODE
     , USER_ID
     , USER_PW
     , USER_NM
  FROM USER
 WHERE 1=1
   AND CO_CODE  = #{coCode}
   AND USER_ID  = #{userId}

mapper.insert:
/* com.biz.framework.mapper.system.RoleMapper.saveRoles */
INSERT INTO ROLE (
    CO_CODE
    , ROLE_NM
    , ROLE_DESC
) VALUES (
    #{loginCoId}
    , #{roleNm}
    , #{roleDesc}
)

mapper.update:
/* com.biz.framework.mapper.system.RoleMapper.updateRoles */
UPDATE ROLE
   SET ROLE_NM       = #{roleNm}
     , ROLE_DESC     = #{roleDesc}
 WHERE CO_CODE       = #{loginCoId}
   AND ROLE_ID       = #{roleId}

mapper.delete:
/* com.biz.framework.mapper.system.RoleMapper.deleteRoles */
DELETE FROM ROLE
 WHERE CO_CODE      = #{loginCoId}
   AND ROLE_ID     = #{roleId}

mapper.insert:
/* com.biz.framework.mapper.system.RoleMapper.deleteRoles */
INSERT INTO USER
(
    CO_CODE
    , USER_ID
    , USER_PW
    , USER_NM
)
VALUES
(
    #{loginCoId}
    , #{userId}
    , #{userPw}
    , #{userNm}
) ON DUPLICATE KEY UPDATE
     USER_PW    = #{userPw}
    , USER_NM   = #{userNm}

mapper.delete:
/* com.biz.framework.mapper.system.UserMapper.deleteUser */
DELETE FROM USER
WHERE CO_CODE   = #{loginCoId}
  AND USER_ID = #{userId}
